var mongoose = require('mongoose');
var Schema = mongoose.Schema;

// making schemas for inserting data in mongodb
var users = Schema({
    first_name: String,
    last_name: String,
    username: String,
    password: String,
    email: String,
    permissions: Array
});
var rates = Schema({
    user_id: String,
    movie_rate: Array

});
var movies = Schema({
    "Title": String,
    "Year": String,
    "Rated": String,
    "Released": String,
    "Runtime": String,
    "Genre": String,
    "Director": String,
    "Writer": String,
    "Actors": String,
    "Plot": String,
    "Language": String,
    "Country": String,
    "Awards": String,
    "Poster": String,
    "Metascore": String,
    "imdbRating": String,
    "imdbVotes": String,
    "imdbID": String,
    "Type": String,
    "Response": String,

});
logs = Schema({
    log: String,
    type: String
});
permissions = Schema({
    title: String,
    name: String
});


module.exports = (function() {
    var _return = {};
    _return.users = mongoose.model('users', users);
    _return.rates = mongoose.model('rates', rates);
    _return.movies = mongoose.model('movies', movies);
    _return.logs = mongoose.model('log', logs);
    _return.permissions = mongoose.model('permissions', permissions);
    return _return;
})();
